package loops;

public class Repeticao {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10, b=20;
		
		
		
		if (a==b) {
			
			System.out.println("a==b");
		}else if (a < b){
			
			System.out.println("a<b");
		}else {
			System.out.println("a maior que b");
		}
	
		

	}

}
